import React from 'react'
import styles from './Input.module.css'

const Input = (props) => {
    return (
        <>
            <div className={styles.input}>
                <label htmlFor={props.input.id}>
                    {props.lable}
                </label>
                <input type='number' max='5' min='1' step='1' defaultValue='1' />
            </div>
        </>
    )
}

export default Input
